import React from 'react';

class Listado extends Component{
    constructor(props){
        super(props);
        this.state = {
            value: '',
            carrito : []
        }
    }

    

    /*handleChange(event) {
        this.setState({value: event.target.value});
      }*/
    
      handleSubmitAgregar(item) {
        //window.location.href='/detalle'
        this.setState((prevState) => ({
            URLArray: [...prevState.carrito, item]
          }));
        localStorage.setItem("carrito", JSON.stringify(this.state.URLArray))

    }

    handleSubmitDetalle(event) {
        window.location.href='/detalle'
    }

    item(data){
        return(
            data.map(item=>
                <div className="col-3">
                        <div className="ProductCard">
                            <div className="ProductCard-image">
                                <img src={item.image} width="250px" height="250px"/>
                            </div>
                            <div className="detalle">

                                <span class="ProductName">
                                    <span class="ProductName-primary">
                                    {item.productId}
                                    </span>
                                    <span class="ProductName-alt">
                                    {item.productName}
                                            <span class="ProductName-separator">•
                                            </span>
                                            {'S/ ' + item.price}
                                    </span>
                                    </span>

                                <div className="content-button">
                                    <button className="btn-oe btn-oe-pay" onClick={this.handleSubmitAgregar(item)}>Agregar</button>
                                    <button className="btn-oe btn-oe-pay" onClick={this.handleSubmitDetalle}>Ver detalle</button>
                                </div>
                            </div>
                            
                        </div>
                </div>
                )
        );
    }

    

    render() {
        const {list} = this.props 
        return (
          <div className="row">
                {this.item(list)}
          </div>
        );
      }

      /*                   
      <div className="cards">
        <div className="content-card">
            <div className="img">
                <img src={item.image} width="50" height="50"/>
            </div>
    <div className="detalle">
        <p>
            {item.productId} {item.productName} {'S/ ' + item.price}
            </p>*/

}


  
  export default Listado;
