import React, { Component } from "react";
import Webcam from "react-webcam";
// import { isAndroid, isIOS, isChrome, isSafari } from "react-device-detect";
/*import imageCompression from "browser-image-compression";*/

const videoConstraints = {
  height: 1080, //set pic resolution
  width: 1920, //set pic resolution
  facingMode: "environment" //use back camera
  // facingMode: "user" //use front camera
};

const toBase64 = (file) =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

class WebCamCustom extends Component {
  constructor(props) {
    super(props);
    this.webcamRef = React.createRef();
  }

  state = {
    url: null,
    NewZipPic: null,
    URLArray: []
  };

  componentDidMount() {
    if (navigator.permissions && navigator.permissions.query) {
      navigator.permissions
        .query({ name: "camera" })
        .then((permissionObj) => {
          if (permissionObj.state !== "granted") {
            // Show how to grant accress right to browser
            alert("Show Alert box, teaching how to allow camera");
          }
          console.log(permissionObj.state);
        })
        .catch((error) => {
          console.log("Got error :", error);
        });
    }
  }

  bytesToSize = (bytes) => {
    var sizes = ["Bytes", "KB", "MB", "GB", "TB"];
    if (bytes == 0) return "0 Byte";
    var i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
    return Math.round(bytes / Math.pow(1024, i), 2) + " " + sizes[i];
  };

  capturePhoto = () => {
    const imageSrc = this.webcamRef.current.getScreenshot();
    //this.compressPhoto(imageSrc);
    this.getImage(imageSrc);
  };

  /*compressPhoto = (pic) => {
    //https://www.npmjs.com/package/browser-image-compression
    var options = {
      maxSizeMB: 0.5,
      // maxWidthOrHeight: 200,
      useWebWorker: true
      // fileType: "image/png"
    };

    imageCompression
      .getFilefromDataUrl(pic)
      .then((file) => imageCompression(file, options))
      .then(toBase64)
      .then((newZipPic) => {
        // console.log(pic);
        this.setState({ url: pic, NewZipPic: newZipPic }, () =>
          this.savePhoto()
        );
      })
      .catch(function (error) {
        console.log(error.message);
      });
  };*/

  getImage(pic){
    this.setState({ url: pic}, () =>
        this.savePhoto()
    );
  }

  savePhoto = () => {
    var imageSrc = this.state.url;
    //var newURL = this.state.NewZipPic;
    /*console.log(
      "Pic size before compress: " + this.bytesToSize(imageSrc.length)
    ); //size before compress
    console.log("Pic size after compress: " + this.bytesToSize(newURL.length)); //size after compress
    this.setState((prevState) => ({
      URLArray: [...prevState.URLArray, newURL]
    }));*/
    console.log(toBase64(imageSrc));

    this.setState((prevState) => ({
        URLArray: [...prevState.URLArray, imageSrc]
      }));

  };

  selectPic = (pic) => {
    //click pic to select that pic, and remove others
    const picIndex = this.state.URLArray.indexOf(pic);
    console.log(picIndex);
    if (picIndex > -1) {
      let newURLArray = this.state.URLArray.splice(picIndex, 1);
      this.setState({ URLArray: newURLArray });
    }
  };

  render() {
    return (
      <>
        <Webcam
          //https://www.npmjs.com/package/react-webcam
          ref={this.webcamRef}
          audio={false}
          screenshotQuality={1} // set the Quality of camera (0-1)
          forceScreenshotSourceSize
          // screenshotFormat="image/png"
          screenshotFormat="image/jpeg"
          className="webCam"
          videoConstraints={videoConstraints} //cameraSetting,eg:resolution, use which camera
          // onUserMedia={(e) => console.log(e)} // show info of media stream
        />

        <button onClick={this.capturePhoto}>Capture</button>
        {this.state.URLArray.map((x) => (
          <div id={x}>
            <img
              className="webCam"
              src={x} //show pic in state
              alt="Screenshot"
              onClick={() => this.selectPic(x)} // click pic to select that pic, and remove others
            />
          </div>
        ))}
      </>
    );
  }
}

export default WebCamCustom;
